SELECT policy_name, sr.opt, avg(runs.generation_duration_in_millis), std(runs.generation_duration_in_millis), avg(sr.compilation_duration_in_millis), std(sr.compilation_duration_in_millis), avg(sr.run_duration_in_micros), std(sr.run_duration_in_micros)  FROM runs JOIN policies p on runs.policy_id = p.policy_id JOIN sub_runs sr on runs.run_id = sr.run_id GROUP BY runs.policy_id, sr.opt